// src/mocks/handlers.js
import { rest } from 'msw';

export const handlers = [
  // カテゴリ取得
  rest.get('/api/categories', (req, res, ctx) => {
    return res(
      ctx.status(200),
      ctx.json(['仕事', '家事', '勉強', '趣味', '未分類'])
    );
  }),

  // タスク作成
  rest.post('/api/tasks', (req, res, ctx) => {
    const { text, priority, category, dueDate } = req.body;
    return res(
      ctx.status(201),
      ctx.json({
        id: Date.now(),
        text,
        completed: false,
        priority,
        category,
        dueDate,
      })
    );
  }),
];
