};

export const notifyErrorMessage = (message) => {
  console.log('üì£ notifyErrorMessage called with:', message);
  if (notifyError) {
    notifyError(message);
  } else {
    console.warn('notifyError „ÅåÁôªÈå≤„Åï„Çå„Å¶„ÅÑ„Åæ„Åõ„Çì:', message);
