import { useState, useEffect,useRef } from "react";
import TaskInput from "./component/TaskInput";
import { TaskContext } from "./context/TaskContext";
import { useCategories } from "./hooks/useCategories"; 
import CategoryInput from "./component/CategoryInput";
import { useContext } from "react"; 
import TaskListWrapper from "./component/TaskListWrapper";
// const SECRET_KEY = process.env.REACT_APP_SECRET_KEY;
import 'react-calendar/dist/Calendar.css';
import CalendarTodo from "./page/Calender";
import { useTasks } from "./hooks/useGetTasks";
import { getTasks, addTask, updateTask, deleteTask ,getCategorie, getCategories,deleteCategory} from './services/TaskService';


export default function TodoApp() {
  const { state, dispatch } = useContext(TaskContext);
  const [tasks, setTasks] = useState([]);
  // const { tasks, editText ,dueDate ,isLoading,categoryFilter} = state;
  const {  editText ,dueDate ,isLoading,categoryFilter} = state;
  // const isFirstRender = useRef(true);
  const [task, setTask] = useState("");
  const [priority, setPriority] = useState(2);
  const [selectedDate, setSelectedDate] = useState(new Date());
  const { fetchTasks } = useTasks();

fetchTasks(); 

  const {
    categories,
    setCategories,
    selectedCategory,
    setSelectedCategory,
    newCategory,
    setNewCategory,
    addCategory
  } = useCategories(tasks, (updatedTasks) =>
    dispatch({ type: "INIT_TASKS", payload: updatedTasks })
  );

  const validateTask = ({ text, priority, category, dueDate }) => {
    if (!text.trim()) {
      alert("„Çø„Çπ„ÇØÂêç„ÅØÂøÖÈ†à„Åß„Åô„ÄÇ");
      return false;
    }

    if (text.length > 100) {
      alert("„Çø„Çπ„ÇØÂêç„ÅØ100ÊñáÂ≠ó‰ª•ÂÜÖ„Å´„Åó„Å¶„Åè„Å†„Åï„ÅÑ„ÄÇ");
      return false;
    }

    if (![1, 2, 3].includes(priority)) {
      alert("ÂÑ™ÂÖàÂ∫¶„Åå‰∏çÊ≠£„Åß„Åô„ÄÇ");
      return false;
    }

    if (!category || category.trim() === "") {
      alert("„Ç´„ÉÜ„Ç¥„É™„ÇíÈÅ∏Êäû„Åó„Å¶„Åè„Å†„Åï„ÅÑ„ÄÇ");
      return false;
    }

    if (dueDate) {
      const selected = new Date(dueDate);
      const today = new Date();
      today.setHours(0, 0, 0, 0); // ‰ªäÊó•„ÅÆ0:00
      if (selected < today) {
        alert("ÊúüÈôê„ÅØÊú™Êù•„ÅÆÊó•‰ªò„ÇíÈÅ∏„Çì„Åß„Åè„Å†„Åï„ÅÑ„ÄÇ");
        return false;
      }
    }

  return true;
};

  
  const handleAddTask = async () => {
    // console.log("handleAddTask:priority",priority)
    // const value = Number(priority);
    // console.log("ÈÅ∏Êäû„Åï„Çå„ÅüÂÑ™ÂÖàÂ∫¶:", value);
    const newTask = {
    text: task,
    priority,
    category: selectedCategory || "Êú™ÂàÜÈ°û",//ÂêçÂâç„Å†„Çà„Å≠
    dueDate
  };

    if (!validateTask(newTask)) return;
    const createdTask= await addTask(newTask);

    dispatch({
      type: "ADD_TASK",
      payload:  createdTask ,
    });
    setTask("");
    setPriority(2);
    setSelectedCategory(categories[0] || "");
    dispatch({ type: "SET_DUE_DATE", payload: "" });
  };

  // useEffect(() => {
  //   if (isFirstRender.current) return; 
    
  //   try {
  //     localStorage.setItem("categories", JSON.stringify(categories));
  //   } catch (error) {
  //     console.error("„Ç´„ÉÜ„Ç¥„É™‰øùÂ≠ò„Ç®„É©„Éº:", error);
  //   }
  // }, [categories]);

  const removeCategory = async(id, title) => {
    if (title === "Êú™ÂàÜÈ°û") return;
  
  try {
    await deleteCategory(id); 
    const res = await getTasks(); 
    const catRes = await getCategories();

    setCategories(catRes.data);
    dispatch({ type: "INIT_TASKS", payload: res });
  } catch (err) {
    console.error("„Ç´„ÉÜ„Ç¥„É™ÂâäÈô§„Ç®„É©„Éº", err);
  }
  };

  const handleCategoryFilterChange = (category) => {
    const updatedFilter = categoryFilter.includes(category)
      ? categoryFilter.filter((cat) => cat !== category) 
      : [...categoryFilter, category]; 
      console.log("Êñ∞„Åó„ÅÑ„Ç´„ÉÜ„Ç¥„É™„Éï„Ç£„É´„Çø„Éº,handleCategoryFilterChange:", updatedFilter);
    dispatch({ type: "SET_CATEGORY_FILTER", payload: updatedFilter });
  };

  const tasksForSelectedDate = tasks.filter(
    (task) =>
      task.dueDate &&
      new Date(task.dueDate).toDateString() === selectedDate.toDateString()
  );

  useEffect(() => {
    console.log("„Çø„Çπ„ÇØÊõ¥Êñ∞:", tasks);
  }, [tasks]);
  
  return (

    <div className="p-4 max-w-md mx-auto bg-white shadow rounded-lg">
      <h1 className="text-xl font-bold mb-4">ToDo „Ç¢„Éó„É™</h1>

      {isLoading ? (
      <p>Ë™≠„ÅøËæº„Åø‰∏≠...</p>
    ) : (
      <>
      <div className="my-4">
        <h2 className="text-lg font-semibold mb-2">üìÖ „Ç´„É¨„É≥„ÉÄ„Éº„Åã„ÇâÊó•‰ªò„ÇíÈÅ∏Êäû</h2>
        <CalendarTodo />
      </div>

      <TaskInput
        task={task}
        setTask={setTask}
        priority={priority}
        handlePriorityChange={(e) => setPriority(Number(e.target.value))} 
        setPriority={setPriority}
        selectedCategory={selectedCategory}
        setSelectedCategory={setSelectedCategory}
        categories={categories}
        handleAddTask={handleAddTask}
        dueDate = {dueDate}
        setDueDate={(date) => dispatch({ type: "SET_DUE_DATE", payload: date })}
        handleCheckboxChange={handleCategoryFilterChange}
        categoryFilter={categoryFilter}
      />

      <TaskListWrapper
        tasks={tasksForSelectedDate}
        categories={categories}
        editText={editText}
        dueDate={dueDate}
        removeCategory={removeCategory}
      />

      <CategoryInput
        newCategory = {newCategory}
        setNewCategory = {setNewCategory}
        addCategory = {addCategory}
      />
      </>
      )}
  </div>
  );
}
