import apiClient from './apiClient';

export const getTasks = async () => {
try {
    const res = await apiClient.get('/tasks');
    return res.data;
  } catch (err) {
    console.error('タスク取得エラー:', err);
    throw err;
  }
};

export const addTask = async (taskDate) => {
  const res = await apiClient.post('/tasks', taskDate);
  return res.data;
};

export const updateTaskAPI = async (id, updatedData) => {
  const res = await apiClient.patch(`/tasks/${id}`, updatedData);
  return res.data;
};

export const deleteTaskAPI = async (id) => {
  await apiClient.delete(`/tasks/${id}`);
};

//カテゴリー
export const getCategories = async () => {
try {
    const res = await apiClient.get('/categories');
    return res.data;
  } catch (err) {
    console.error('カテゴリー取得エラー:', err);
    throw err;
  }
};

export const addCategoryAPI = async (title) => {
  const res = await apiClient.post('/categories', { title });
  return res.data;
};

export const updateCategory = async (id, updatedData) => {
  const res = await apiClient.put(`/categories/${id}`, updatedData);
  return res.data;
};

export const deleteCategory = async (id) => {
  await apiClient.delete(`/categories/${id}`);
};
