import { createContext, useReducer,useEffect, useState} from "react";
import { getCategories, addCategoryAPI } from "../services/TaskService";
export const TaskContext = createContext();

const initialState = {
  tasks: [],
  editText: "",
  // dueDate: "",
  isLoading: true,
  categoryFilter: [],
  sortBy: "deadlineAsc", 
};

const taskReducer = (state, action) => {
    switch(action.type) {
        case "INIT_TASKS":
            return {
                ...state,
                tasks: action.payload,
                isLoading: false
            };
        case "ADD_TASK":
          console.log("ADD_TASK")
            return {
              ...state,
              tasks: [...(state.tasks || []), action.payload],
            };
        case "DELETE_TASK":
          return{
              ...state,
              tasks: state.tasks.filter(task => task._id !== action.payload.id)
          };
        case "START_EDITING":
            return {
              ...state,
              tasks: state.tasks.map(task =>
                task.id === action.payload.id
                  ? { ...task, isEditing: true }
                  : task
              ),
              editText: action.payload.text,
            };
        case "SAVE_EDIT":
            return {
                ...state,
                tasks: state.tasks.map(task =>
                task._id === action.payload.id
                    ? { ...task, text: action.payload.text, isEditing: false }
                    : task
                ),
                  editText:""
            };
        case "TOGGLE_TASK":
            return {
                ...state,
                tasks: state.tasks.map(task =>
                    task.id === action.payload.id
                    ? { ...task, completed: !task.completed }
                    : task
                ),
                    editText:""
            };
        case "UPDATE_PRIORITY":
            return {
                ...state,
                tasks: state.tasks.map(task =>
                task.id === action.payload.id
                    ? { ...task, priority: action.payload.newPriority }
                    : task
                ),
            };
        case "SET_DUE_DATE":
              return {
                  ...state,
                  dueDate: action.payload,
              };
        case "UPDATE_DUE_DATE":
              return {
                  ...state,
                  tasks: state.tasks.map(task =>
                    task.id === action.payload.id
                      ? { ...task, dueDate: action.payload.dueDate }
                      : task
                  )
                };
        case "TOGGLE_CATEGORY_FILTER":
              const { payload: category } = action;
              const isSelected = state.categoryFilter.includes(category);
              return {
                ...state,
                categoryFilter: isSelected
                ? state.categoryFilter.filter(c => c !== category) // 解除
                : [...state.categoryFilter, category],             // 追加
          };
        case 'SET_CATEGORY_FILTER':
          console.log('新しいカテゴリフィルター:', action.payload); 
            return {
                ...state,
                categoryFilter: action.payload,
            };
        case "SET_SORT":
            return {
                ...state,
                sortBy: action.payload,
            };
      default:
        return state;
    }
  };

  export const TaskProvider = ({ children }) => {

  const [state, dispatch] = useReducer(taskReducer, initialState);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("");
  const [newCategory, setNewCategory] = useState("");

  // 初期取得
  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const category = await getCategories();
        setCategories(category.data);
        setSelectedCategory(category[0]?.id || "");
      } catch (err) {
        console.error("カテゴリ取得失敗:", err);
      }
    };
    fetchCategories();
  }, []);

  const addCategory = async () => {
    if (newCategory && !categories.some(c => c.title === newCategory)) {
      try {
        console.log("新しいカテゴリー",newCategory)
        await addCategoryAPI(newCategory);
        const updated = await getCategories();
        // console.log("新しいカテゴリー追加再取得",newCategory)
        console.log("新しいカテゴリー追加再取得",updated)
        setCategories(updated.data);
        // setCategories(prev => [...prev, newCategory]); 
        setNewCategory("");
      } catch (err) {
        console.error("カテゴリ追加失敗:", err);
      }
    }
  };
       
return (
       <TaskContext.Provider
      value={{
        state,
        dispatch,
        categories,
        setCategories,
        selectedCategory,
        setSelectedCategory,
        newCategory,
        setNewCategory,
        addCategory
      }}
    >
      {children}
    </TaskContext.Provider>
  );
}
