import apiClient from './apiClient';

//新規登録
export const registerUser = async (registerData) => {
  try {
    const res = await apiClient.post("/auth/register", registerData);
    return res.data;
  } catch (err) {
    console.error('新規登録エラー:', err);
    throw err;
  }
};

//ログイン
export const loginUser = async (loginData) => {
  const res = await apiClient.post("/auth/login", loginData);
  return res.data;
};

//ログアウト
export const logoutUser = async (logoutData) => {
  const res = await apiClient.post("/auth/logout", logoutData);
  return res.data;
};
